mapper.select:
SELECT tus.user_shanghu_id, tus.userName, tus.youName, tus.phone, tus.xianjinAmount, tus.integral, tus.registerTime, tus.ip, tus.last_login_time, tus.bz, tus.top, tus.`status`, tus.renZhengApplyTime, tus.renZhengBackTime, tus.renZhengState FROM tb_user_shanghu tus
identity=#{pd.identity}
and tus.phone LIKE CONCAT(CONCAT('%', #{pd.searchName}),'%') or tus.userName LIKE CONCAT(CONCAT('%', #{pd.searchName}),'%')
and tus.renZhengState=#{pd.renZhengState}
ORDER BY tus.top desc, tus.last_login_time DESC

