mapper.select:
SELECT tus.userName, tus.phone, tus.identity, twc.withdraw_cash_id, twc.serial_number, twc.amount, twc.shenqing_time, twc.tixian_method, twc.account, twc.realName, twc.feedback_time, twc.state, twc.imgPath, twc.caozuoName FROM tb_withdraw_cash twc LEFT JOIN tb_user_shanghu tus ON tus.user_shanghu_id=twc.user_fid
twc.type="2" and twc.state=#{pd.state}
and twc.serial_number=#{pd.searchName}
ORDER BY twc.shenqing_time ASC

SELECT tuk.userName, tuk.phone, twc.withdraw_cash_id, twc.serial_number, twc.amount, twc.shenqing_time, twc.tixian_method, twc.account, twc.realName, twc.feedback_time, twc.state, twc.imgPath, twc.caozuoName FROM tb_withdraw_cash twc LEFT JOIN tb_user_kehu tuk ON tuk.user_kuhu_id=twc.user_fid
twc.type="1" and twc.state=#{pd.state}
and twc.serial_number=#{pd.searchName}
ORDER BY twc.shenqing_time ASC

