mapper.update:
UPDATE tb_user_shanghu tus
SET 
	tus.chengjiaoAmount = #{chengjiaoAmount},
	tus.xianjinAmount = #{xianjinAmount}
WHERE
	tus.user_shanghu_id = #{user_shanghu_id}

mapper.select:
SELECT tus.userName ysuserName, tus.phone ysPhone, tuk.userName khuserName, tuk.phone khPhone, toy.create_order_time, toy.order_number, toy.fuwu_jibing_name, toy.jibing_introdaction, toy.price, toy.pay_method, toy.pay_number, toy.complete_time, toy.order_state FROM tb_order_yisheng toy LEFT JOIN tb_user_shanghu tus ON tus.user_shanghu_id = toy.user_shanghu_fid LEFT JOIN tb_user_kehu tuk ON tuk.user_kuhu_id = toy.user_kuhu_fid WHERE tus.identity = '1'
and toy.order_number=#{pd.searchName}
ORDER BY toy.create_order_time DESC

SELECT tus.userName ysuserName, tus.phone ysPhone, tuk.userName khuserName, tuk.phone khPhone, toy.create_order_time, toy.order_number, toy.fuwu_name, toy.fuwu_introdaction, toy.price, toy.pay_method, toy.pay_number, toy.complete_time, toy.order_state FROM tb_order_jigou toy LEFT JOIN tb_user_shanghu tus ON tus.user_shanghu_id = toy.user_shanghu_fid LEFT JOIN tb_user_kehu tuk ON tuk.user_kuhu_id = toy.user_kuhu_fid WHERE tus.identity = '2'
and toy.order_number=#{pd.searchName}
ORDER BY toy.create_order_time DESC

SELECT tus.userName ysuserName, tus.phone ysPhone, tuk.userName khuserName, tuk.phone khPhone, toy.create_order_time, toy.order_number, toy.fuwu_name, toy.fuwu_introdaction, toy.price, toy.pay_method, toy.pay_number, toy.complete_time, toy.order_state FROM tb_order_yaoshang toy LEFT JOIN tb_user_shanghu tus ON tus.user_shanghu_id = toy.user_shanghu_fid LEFT JOIN tb_user_kehu tuk ON tuk.user_kuhu_id = toy.user_kuhu_fid WHERE tus.identity = '1'
and toy.order_number=#{pd.searchName}
ORDER BY toy.create_order_time DESC

SELECT tus.userName shuserName, tus.phone shPhone, tus.identity, tuk.userName khuserName, tuk.phone khPhone, toy.create_order_time, toy.order_number, toy.fuwu_jibing_name, toy.jibing_introdaction, toy.kehuPrice price, toy.pay_method, toy.pay_number, toy.complete_time, toy.order_state FROM tb_order_yisheng toy LEFT JOIN tb_user_shanghu tus ON tus.user_shanghu_id = toy.user_shanghu_fid LEFT JOIN tb_user_kehu tuk ON tuk.user_kuhu_id = toy.user_kuhu_fid
toy.order_number=#{pd.searchName}
ORDER BY toy.create_order_time DESC
